mapper.update:

update t_rlation
set UserID = #{userid,jdbcType=VARCHAR},
CommunityID = #{communityid,jdbcType=VARCHAR},
Position = #{position,jdbcType=VARCHAR},
Department= #{department,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:

select ID, UserID, CommunityID, Position,Department
from t_rlation
where ID = #{id,jdbcType=VARCHAR}


select ID, UserID, CommunityID, Position,Department
from t_rlation
where CommunityID = #{0,jdbcType=VARCHAR}
and UserID = #{1,jdbcType=VARCHAR}

select ID, UserID, CommunityID, Position,Department
from t_rlation
where CommunityID=#{communityId,jdbcType=VARCHAR} and UserID=#{userId,VARCHAR}
